import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom'



export default function SignIn() {

    const loginApiUrl = 'http://test-demo.aemenersol.com/api/account/login';
    
    //const apiUrl = 'https://jsonplaceholder.typicode.com/todos';

    const [userCred, setUserCred] = useState({
        username: '',
        password: ''
    })

    const navigate = useNavigate();

    function handleChange(event) {
        setUserCred(prevCred => {
            return {
                ...prevCred,
                [event.target.id]: event.target.value
            }
        })

        console.log(userCred)
    }

    async function fetchToken(url, cred) {
        
        const response = await fetch(url, {
            method: 'POST',
            body: JSON.stringify(cred),
            headers: {
                "Content-type": "application/json"
            }
        })

        //const response = await fetch(url)

        const token = await response.json();
        sessionStorage.setItem('aemToken', token)

        navigate('/dashboard')
        console.log(token)
    }

    

    function handleSubmit(event) {
        event.preventDefault()

        fetchToken(loginApiUrl, userCred);
        //const token = 'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9..aUYxePVp01e6YajB0M9d3dLsRFpl6tmBsfc2-C9q7Wk';

        
        
    }
    
    return (
        <div className="sign-in-container">
            <h1>Sign in to your dashboard</h1>
            <form onSubmit={handleSubmit}>
                <input name="username" id="username" placeholder='Username' required onChange={handleChange} value="user@aemenersol.com" />
                <input type="password" name="password" id="password" placeholder='Password' required onChange={handleChange} />
                <input type="submit" value="SIGN IN" onSubmit={handleSubmit} />
            </form>
        </div>
    )
}
