import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom'
import '../Styles/signin.css'


export default function SignIn() {

    const loginApiUrl = 'http://test-demo.aemenersol.com/api/account/login';

    const [userCred, setUserCred] = useState({
        username: 'user@aemenersol.com',
        password: 'Test@123'
    })
    const [isLogginIn, setIsLogginIn] = useState(false)

    const navigate = useNavigate();

    function handleChange(event) {
        setUserCred(prevCred => {
            return {
                ...prevCred,
                [event.target.id]: event.target.value
            }
        })
    }

    async function fetchToken(url, cred) {
        
        const response = await fetch(url, {
            method: 'POST',
            body: JSON.stringify(cred),
            headers: {
                "Content-type": "application/json"
            }
        })

        const token = await response.json();
        sessionStorage.setItem('aemToken', token)

        navigate('/dashboard')
        console.log(token)
    }

    

    function handleSubmit(event) {
        event.preventDefault()

        fetchToken(loginApiUrl, userCred);
    }
    
    return (
        <div id="sign-in">
            <form id="sign-in-form" onSubmit={handleSubmit}>
                <div id="sign-in-overlay">

                </div>
                <div className="form-group" id="form-title-container">
                    <h2 className="form-title">Sign in to your dashboard</h2>
                </div>
                <div className="form-group" id="input-container">
                    <input name="username" id="username" className="user-input" placeholder='Username' required onChange={handleChange} value="user@aemenersol.com" />
                    <input type="password" name="password" id="password" className="user-input" placeholder='Password' required onChange={handleChange} value="Test@123" />
                    <input type="submit" id="submit-btn" value="SIGN IN" onSubmit={handleSubmit} />
                </div>
            </form>
        </div>
    )
}
