import React, { useEffect, useState, useRef, useCallback } from 'react';
import * as d3 from 'd3';
import { useNavigate } from 'react-router-dom'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faArrowRightFromBracket } from '@fortawesome/free-solid-svg-icons'
import '../Styles/dashboard.css'


const dashData = {
    chartBar: [
        {'Bar 1': 30},
        {'Bar 2': 60},
        {'Bar 3': 56},
        {'Bar 4': 27},
        {'Bar 5': 5},
        {'Bar 6': 29},
    ],
    chartDonut: [
        {'Donut 1': 25},
        {'Donut 2': 22},
        {'Donut 3': 29},
        {'Donut 4': 8},
    ],
    tableUsers: [
        {
            'firstName': 'John',
            'lastName': 'Doe',
            'username': 'john_doe'
        },
        {
            'firstName': 'John',
            'lastName': 'Doe',
            'username': 'john_doe2'
        },
        {
            'firstName': 'John',
            'lastName': 'Doe',
            'username': 'john_doe3'
        }
    ]
}



const dataApiUrl = 'http://test-demo.aemenersol.com/api/dashboard';

export default function DashBoard() {

    const navigate = useNavigate();

    const [dashData, setDashData] = useState(null);
    const [loggedIn , setLoggedIn] = useState(false)
    const [windowSize, setWindowSize] = useState(window.innerWidth)

    const barChart = useRef();
    const donutChart = useRef();

    const handleWindowResize = useCallback(event => {

        setWindowSize(window.innerWidth);
    
    }, []); 
    


    async function fetchDashData(url, token) {


        const response = await fetch(url, {
            method: 'GET',
            headers: {
                'Authorization': `Bearer ${token}`
            }
        })

        const data = await response.json();
        setDashData(data)

        
    }

    function handleSignOut() {
        sessionStorage.removeItem('aemToken')
        setLoggedIn(false)
    }

    function plotBarChart(ref, data) {
        const chart = d3.select(ref.current)

        const w = document.getElementsByClassName("donut-chart")[0].getClientRects()[0].width
        const h = document.getElementsByClassName("donut-chart")[0].getClientRects()[0].height
        const padding = w*0.1;

        const yScaleBar = d3.scaleLinear()
                            .domain([0, d3.max(data, d => d.value)])
                            .range([h-padding, padding])
        const xScaleBar = d3.scaleLinear()
                            .domain([0, data.length])
                            .range([padding, w-padding])

        chart.selectAll('rect')
            .data(data)
            .enter()
            .append('rect')
            .attr('class', 'bar')
            .attr('x', (d, i) => xScaleBar(i))
            .attr('y', d => yScaleBar(d.value))
            .attr('width', (w - 2*padding) / (1.5 * data.length - 0.5))
            .attr('height', d => h - padding - yScaleBar(d.value))

        const x_axis = d3.axisBottom().scale(xScaleBar)
        const y_axis = d3.axisLeft().scale(yScaleBar)

        chart.append('g').attr('transform', `translate(0, ${h-padding})`).call(x_axis)
        chart.append('g').attr('transform', `translate(${padding}, 0)`).call(y_axis)
    }

    function plotDonutChart(ref, data) {
        const chart = d3.select(ref.current)

        const w = document.getElementsByClassName("donut-chart")[0].getClientRects()[0].width
        const h = document.getElementsByClassName("donut-chart")[0].getClientRects()[0].height
        const padding = w*0.1;
        const radius = (Math.min(w , h) - 2*padding) / 2;

        chart.attr('viewBox', `0 0 ${Math.min(w,h)} ${Math.min(w,h)}`)
            .attr('preserveAspectRatio', 'xMinYMin')
            .append('g')
            .attr('transform', `translate(${Math.min(w,h) / 2}, ${Math.min(w,h) / 2})`)

        const color = d3.scaleOrdinal()
                        .domain(data)
                        .range(['#4daf4a','#377eb8','#ff7f00','#984ea3','#e41a1c'])

        const pie = d3.pie()
                    .sort(null)
                    .value(d => d.value)

        const arc = d3.arc()
                    .innerRadius(radius-radius*0.5)
                    .outerRadius(radius-radius*0.1)

        const arcs = pie(data)

        chart.selectAll('path')
            .data(arcs)
            .enter()
            .append('path')
            .attr('fill', (d,i) => color(i))
            .attr('transform', `translate(${w/2}, ${h/2})`)
            .attr('stroke', 'white')
            .attr('d', arc)
    }

    useEffect(() => {
        const token = sessionStorage.getItem('aemToken')
        
        if (token == null) {
            navigate('/')
        } else {
            fetchDashData(dataApiUrl, token)
            setLoggedIn(true)
        }

    }, [loggedIn])

    useEffect(() => {
        if (dashData != null) {
            // plot bar chart
            plotBarChart(barChart, dashData.chartBar)
            plotDonutChart(donutChart, dashData.chartDonut)
        }
    }, [dashData, windowSize])


    return (
        <div>
            <nav id="dash-nav">
                <h2 id="nav-title">Dashboard</h2>
                <button id="sign-out-btn" onClick={handleSignOut}><FontAwesomeIcon icon={faArrowRightFromBracket} className="sign-out-icon" />Sign out</button>
            </nav>
            <section id="dashboard">
                <h1>Overview</h1>
                {dashData && 
                    <div id="dash-main-container">
                        <div id="card-container">
                            <div className="card svg-container">
                                <h3>Chart Title</h3>
                                {/*<svg className="chart svg-content donut-chart" height="400" width="100%" ref={donutChart} preserveAspectRatio="xMinYMin meet" viewBox='0 0 500 500' />*/}
                                <svg className="chart svg-content donut-chart" height="400" width="100%" ref={donutChart} />
                            </div>
                            <div className="card svg-container">
                                <h3>Chart Title</h3>
                                <svg className="chart svg-content bar-chart" height="400" width="100%" ref={barChart} preserveAspectRatio="xMinYMin meet" viewBox='0 0 500 500' />
                            </div>
                            
                        </div>
                        
                        <div className="card">
                            <table>
                                <tr>
                                    <th>#</th>
                                    <th>First Name</th>
                                    <th>Last Name</th>
                                    <th>User Name</th>
                                </tr>
                                {dashData.tableUsers.map((user, i) => {
                                    return (
                                        <tr>
                                            <td>{i+1}</td>
                                            <td>{user.firstName}</td>
                                            <td>{user.lastName}</td>
                                            <td>{user.username}</td>
                                        </tr>
                                    )
                                })}
                                
                            </table>
                        </div>
                    </div>
                
                }
                
            </section>
            
        </div>
    )
}
