import React, { useEffect, useState, useRef } from 'react';
import * as d3 from 'd3';
import { useNavigate } from 'react-router-dom'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faArrowRightFromBracket } from '@fortawesome/free-solid-svg-icons'
import '../Styles/dashboard.css'


const dashData = {
    chartBar: [
        {'Bar 1': 30},
        {'Bar 2': 60},
        {'Bar 3': 56},
        {'Bar 4': 27},
        {'Bar 5': 5},
        {'Bar 6': 29},
    ],
    chartDonut: [
        {'Donut 1': 25},
        {'Donut 2': 22},
        {'Donut 3': 29},
        {'Donut 4': 8},
    ],
    tableUsers: [
        {
            'firstName': 'John',
            'lastName': 'Doe',
            'username': 'john_doe'
        },
        {
            'firstName': 'John',
            'lastName': 'Doe',
            'username': 'john_doe2'
        },
        {
            'firstName': 'John',
            'lastName': 'Doe',
            'username': 'john_doe3'
        }
    ]
}

const dataApiUrl = 'http://test-demo.aemenersol.com/api/dashboard';

export default function DashBoard() {

    const navigate = useNavigate();

    const [dashData, setDashData] = useState(null);
    const [loggedIn , setLoggedIn] = useState(false)

    const barChart = useRef();
    const donutChart = useRef();


    async function fetchDashData(url, token) {


        const response = await fetch(url, {
            method: 'GET',
            headers: {
                'Authorization': `Bearer ${token}`
            }
        })

        const data = await response.json();
        setDashData(data)

        plotBarChart(barChart, data.chartBar, 0)
    }

    function handleSignOut() {
        sessionStorage.removeItem('aemToken')
        setLoggedIn(false)
    }


    function plotBarChart(ref, data, padding) {
        console.log(data)
        const chart = d3.select(ref.current)

        chart.selectAll('rect')
            .data(data)
            .enter()
            .append('rect')
            .attr('x', (d, i) => i*30)
            .attr('y', d => 200 - d.value)
            .attr('width', 20)
            .attr('height', d => d.value)
    }

    useEffect(() => {
        const token = sessionStorage.getItem('aemToken')
        
        if (token == null) {
            navigate('/')
        } else {
            fetchDashData(dataApiUrl, token)
            setLoggedIn(true)
        }

    }, [loggedIn])


    return (
        <div>
            <nav id="dash-nav">
                <h2 id="nav-title">Dashboard</h2>
                <button id="sign-out-btn" onClick={handleSignOut}><FontAwesomeIcon icon={faArrowRightFromBracket} className="sign-out-icon" />Sign out</button>
            </nav>
            <section id="dashboard">
                <h1>Overview</h1>
                {dashData && 
                    <div id="dash-main-container">
                        <div id="card-container">
                            <div className="card">
                                <svg height="100" ref={barChart} className="bar-chart"></svg>
                            </div>
                            <div className="card">
                                <svg height="100" ref={donutChart} className="donut-chart"></svg>
                            </div>
                        </div>
                        
                        <div className="card">
                            <table>
                                <tr>
                                    <th>#</th>
                                    <th>First Name</th>
                                    <th>Last Name</th>
                                    <th>User Name</th>
                                </tr>
                                {dashData.tableUsers.map((user, i) => {
                                    return (
                                        <tr>
                                            <td>{i+1}</td>
                                            <td>{user.firstName}</td>
                                            <td>{user.lastName}</td>
                                            <td>{user.username}</td>
                                        </tr>
                                    )
                                })}
                                
                            </table>
                        </div>
                    </div>
                
                }
                
            </section>
            
        </div>
    )
}
