import React, { useEffect, useState } from 'react'
import { useNavigate } from 'react-router-dom'


const dashData = {
    chartBar: [
        {'Bar 1': 30},
        {'Bar 2': 60},
        {'Bar 3': 56},
        {'Bar 4': 27},
        {'Bar 5': 5},
        {'Bar 6': 29},
    ],
    chartDonut: [
        {'Donut 1': 25},
        {'Donut 2': 22},
        {'Donut 3': 29},
        {'Donut 4': 8},
    ],
    tableUsers: [
        {
            'firstName': 'John',
            'lastName': 'Doe',
            'username': 'john_doe'
        },
        {
            'firstName': 'John',
            'lastName': 'Doe',
            'username': 'john_doe2'
        },
        {
            'firstName': 'John',
            'lastName': 'Doe',
            'username': 'john_doe3'
        }
    ]
}

const dataApiUrl = 'http://test-demo.aemenersol.com/api/dashboard';

export default function DashBoard() {

    const navigate = useNavigate();

    const [dashData, setDashData] = useState(null)


    async function fetchDashData(url, token) {
        console.log(token)
        const response = await fetch(url, {
            method: 'GET',
            headers: {
                'Authorization': `Bearer ${token}`
            }
        })

        const data = await response.json();

        setDashData(data)
    }


    useEffect(() => {
        const token = sessionStorage.getItem('aemToken')
        
        if (token == null) {
            navigate('/')
        } else {
            fetchDashData(dataApiUrl, token)
        }

    }, [])

    return (
        <div>
            <h1>You are signed in</h1>
            <p>{dashData}</p>
        </div>
    )
}
