import React, { useEffect, useState, useRef } from 'react';
import * as d3 from 'd3';
import { useNavigate } from 'react-router-dom'


const dashData = {
    chartBar: [
        {'Bar 1': 30},
        {'Bar 2': 60},
        {'Bar 3': 56},
        {'Bar 4': 27},
        {'Bar 5': 5},
        {'Bar 6': 29},
    ],
    chartDonut: [
        {'Donut 1': 25},
        {'Donut 2': 22},
        {'Donut 3': 29},
        {'Donut 4': 8},
    ],
    tableUsers: [
        {
            'firstName': 'John',
            'lastName': 'Doe',
            'username': 'john_doe'
        },
        {
            'firstName': 'John',
            'lastName': 'Doe',
            'username': 'john_doe2'
        },
        {
            'firstName': 'John',
            'lastName': 'Doe',
            'username': 'john_doe3'
        }
    ]
}

const dataApiUrl = 'http://test-demo.aemenersol.com/api/dashboard';

export default function DashBoard() {

    const navigate = useNavigate();

    const [dashData, setDashData] = useState(null)


    async function fetchDashData(url, token) {
        console.log(token)
        const response = await fetch(url, {
            method: 'GET',
            headers: {
                'Authorization': `Bearer ${token}`
            }
        })

        const data = await response.json();

        setDashData(data)
    }

    function handleSignOut() {

    }

    useEffect(() => {
        const token = sessionStorage.getItem('aemToken')
        
        if (token == null) {
            navigate('/')
        } else {
            fetchDashData(dataApiUrl, token)
        }

    }, [])

    d3.select('body').append('h1').text('test')

    return (
        <div>
            <nav>
                <h2>Dashboard</h2>
                <button>Sign out</button>
            </nav>
            <section id="dashboard">
                <h1>Overview</h1>
                <div id="dash-main-container">
                    <div className="card">
                    </div>
                    <div className="card">
                        
                    </div>
                    <div className="card">
                        <table>
                            <tr>
                                <th>#</th>
                                <th>First Name</th>
                                <th>Last Name</th>
                                <th>User Name</th>
                            </tr>
                            {dashData.tableUsers.map((user, i) => {
                                return (
                                    <tr>
                                        <td>{i+1}</td>
                                        <td>{user.firstName}</td>
                                        <td>{user.lastName}</td>
                                        <td>{user.username}</td>
                                    </tr>
                                )
                            })}
                            
                        </table>
                    </div>
                </div>
            </section>
            
        </div>
    )
}
