import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom'


export default function SignIn() {

    const loginApiUrl = 'http://test-demo.aemenersol.com/api/account/login';

    const [userCred, setUserCred] = useState({
        username: 'user@aemenersol.com',
        password: 'Test@123'
    })

    const navigate = useNavigate();

    function handleChange(event) {
        setUserCred(prevCred => {
            return {
                ...prevCred,
                [event.target.id]: event.target.value
            }
        })

        console.log(userCred)
    }

    async function fetchToken(url, cred) {
        
        const response = await fetch(url, {
            method: 'POST',
            body: JSON.stringify(cred),
            headers: {
                "Content-type": "application/json"
            }
        })

        const token = await response.json();
        sessionStorage.setItem('aemToken', token)

        navigate('/dashboard')
        console.log(token)
    }

    

    function handleSubmit(event) {
        event.preventDefault()

        fetchToken(loginApiUrl, userCred);
    }
    
    return (
        <div id="sign-in">
            <form onSubmit={handleSubmit}>
                <h1>Sign in to your dashboard</h1>
                <input name="username" id="username" placeholder='Username' required onChange={handleChange} value="user@aemenersol.com" />
                <input type="password" name="password" id="password" placeholder='Password' required onChange={handleChange} value="Test@123" />
                <input type="submit" value="SIGN IN" onSubmit={handleSubmit} />
            </form>
        </div>
    )
}
