import React, { useState } from 'react';

export default function SignIn() {

    const apiUrl = 'http://test-demo.aemenersol.com/api/account/login';
    //const apiUrl = 'https://jsonplaceholder.typicode.com/todos';

    const [userCred, setUserCred] = useState({
        username: '',
        password: ''
    })

    function handleChange(event) {
        setUserCred(prevCred => {
            return {
                ...prevCred,
                [event.target.id]: event.target.value
            }
        })

        console.log(userCred)
    }

    async function postCred(url, cred) {
        
        const response = await fetch(url, {
            method: 'POST',
            body: JSON.stringify(cred),
            headers: {
                "Content-type": "application/json"
            }
        })

        //const response = await fetch(url)

        const token = await response.json()
        console.log(token)


    }

    function handleSubmit(event) {
        event.preventDefault()

        //const data = postCred(apiUrl, userCred);

        
        fetch('http://test-demo.aemenersol.com/api/dashboard', {
            method: 'GET',
            headers: {
                'Authorization': 'Bearer eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9..NSIKQDwvmci3WkzuFVVm9Ixpu8QSNu2tI7rvLzGtViA' 
            }
        })
        .then(res => res.json())
        .then(data => {console.log(data)})
    }
    
    return (
        <div className="sign-in-container">
            <h1>Sign in to your dashboard</h1>
            <form onSubmit={handleSubmit}>
                <input name="username" id="username" placeholder='Username' required onChange={handleChange} />
                <input type="password" name="password" id="password" placeholder='Password' required onChange={handleChange} />
                <input type="submit" value="SIGN IN" onSubmit={handleSubmit} />
            </form>
        </div>
    )
}
