import React, { useState } from 'react';

export default function SignIn() {

    const loginApiUrl = 'http://test-demo.aemenersol.com/api/account/login';
    const dataApiUrl = 'http://test-demo.aemenersol.com/api/dashboard';
    //const apiUrl = 'https://jsonplaceholder.typicode.com/todos';

    const [userCred, setUserCred] = useState({
        username: '',
        password: ''
    })

    function handleChange(event) {
        setUserCred(prevCred => {
            return {
                ...prevCred,
                [event.target.id]: event.target.value
            }
        })

        console.log(userCred)
    }

    async function fetchToken(url, cred) {
        
        const response = await fetch(url, {
            method: 'POST',
            body: JSON.stringify(cred),
            headers: {
                "Content-type": "application/json"
            }
        })

        //const response = await fetch(url)

        const token = await response.json();
        console.log(token)

        fetchDashData(dataApiUrl, token)
    }

    async function fetchDashData(url, token) {
        const response = await fetch(url, {
            method: 'GET',
            headers: {
                'Authorization': `Bearer ${token}`
            }
        })

        const data = await response.json();

        console.log
    }

    function handleSubmit(event) {
        event.preventDefault()

        fetchToken(loginApiUrl, userCred);
    }
    
    return (
        <div className="sign-in-container">
            <h1>Sign in to your dashboard</h1>
            <form onSubmit={handleSubmit}>
                <input name="username" id="username" placeholder='Username' required onChange={handleChange} />
                <input type="password" name="password" id="password" placeholder='Password' required onChange={handleChange} />
                <input type="submit" value="SIGN IN" onSubmit={handleSubmit} />
            </form>
        </div>
    )
}
